import React from "react";

const Profile = () => {
    const [profile, setProfile] = useState({
        firstName: 'John',
        lastName: 'Doe',
        email: 'johndoe@example.com',
        phoneNumber: '555-1234',
        address: '123 Main St',
      });

      // Düzenleme modunu kontrol eden state
      const [editMode, setEditMode] = useState(false);

      // Profil bilgilerini düzenlemek için kullanılan fonksiyon
      const handleEditProfile = () => {
        setEditMode(true);
      };

      // Profil bilgilerini kaydetmek için kullanılan fonksiyon
      const handleSaveProfile = () => {
        setEditMode(false);
        // Profil bilgilerini kaydetmek için API çağrısı yapılabilir veya başka bir işlem gerçekleştirilebilir
      };

      // Profil sayfası render fonksiyonu
      return (
        <div>
          <h1>Profil Sayfası</h1>
          {!editMode ? (
            <div>
              <p>Ad: {profile.firstName}</p>
              <p>Soyad: {profile.lastName}</p>
              <p>E-posta: {profile.email}</p>
              <p>Telefon: {profile.phoneNumber}</p>
              <p>Adres: {profile.address}</p>
              <button onClick={handleEditProfile}>Düzenle</button>
            </div>
          ) : (
            <div>
              <h2>Profil Bilgilerini Düzenle</h2>
              <input
                type="text"
                value={profile.firstName}
                onChange={(e) => setProfile({ ...profile, firstName: e.target.value })}
              />
              <input
                type="text"
                value={profile.lastName}
                onChange={(e) => setProfile({ ...profile, lastName: e.target.value })}
              />
              <input
                type="email"
                value={profile.email}
                onChange={(e) => setProfile({ ...profile, email: e.target.value })}
              />
              <input
                type="tel"
                value={profile.phoneNumber}
                onChange={(e) => setProfile({ ...profile, phoneNumber: e.target.value })}
              />
              <input
                type="text"
                value={profile.address}
                onChange={(e) => setProfile({ ...profile, address: e.target.value })}
              />
              <button onClick={handleSaveProfile}>Kaydet</button>
            </div>
          )}
        </div>
      );
    };

export default Profile
